(1, 1, 0, 'header', '', 0, 1, 0, '<!DOCTYPE html>\n<html lang="ru">\n<head>\n	<meta charset="UTF-8">\n	<title></title>\n	<link rel="stylesheet" href="maket/londium/dist/css/style-shop.css">\n	<link rel="stylesheet" href="maket/londium/dist/css/bootstrap.css">\n   <script src="https://code.jquery.com/jquery-1.10.2.min.js"></script>\n     <script src="maket/londium/dist/js/parallax.js"></script>\n\n</head>\n<body>\n\n<!-- _____________________анимация загрузки, стили с css -->\n\n\n<div id="before-load">\n <div class="befor-load-1"></div>\n <p class="load">\n	<span class="load-1">LONDIUM\n		<br> <span class="load-2">loading</span> 	\n	</span>\n\n </p>\n</div>\n\n<!-- ________________________________END____________________________ -->\n<div class="container-fluid">\n		\n			<div class="header">\n				<div class="row row-1">\n				<div class="col-xs-3 col-sm-3 col-md-3 col-lg-3 link-left">\n					<a href="#">Женщины</a>\n					<a href="#">Мужчины</a>\n				</div>\n				\n				<div class="col-xs-4 col-sm-4 col-md-4 col-lg-4 logo">\n					<a href="#"><img src="maket/londium/img/logo.png" class="center-block" alt="/"></a>	\n				</div>	\n\n				<div class="col-xs-3 col-sm-3 col-md-3 col-lg-3 link-right">\n					<a href="#">Новокузнецк</a>\n					<div class="ico-up"> <img src="maket/londium/img/ico.png" alt=""><a href="#">Магазины</a></div>\n\n				</div>\n				</div>\n			</div>\n\n\n\n', 0, 'a:0:{}', 0, ''),
(2, 1, 0, 'footer', '', 0, 1, 0, '\n	\n	<div class="col-xs-12 col-sm-12 col-md-12 col-lg-12 footer">\n	\n	  <div class="row row-2"> \n	\n		\n	<div class="col-xs-4 col-sm-4 col-md-4 col-lg-4 link-foot">\n		<p>LONDIUM- <br> магазан одежды CASUAL</p>\n	</div>\n	\n	\n	\n	\n	<div class="col-xs-2 col-sm-2 col-md-2 col-lg-2 link-left-2">\n		<a href="#">Женщины</a> <br>\n		<a href="#">Мужчины</a>\n	</div>\n	\n	\n	<div class="col-xs-2 col-sm-2 col-md-2 col-lg-2 link-right-2">\n		<a href="#" class="nvk">Новокузнецк</a>\n	  <div class="ico-1">\n		 <img src="maket/londium/img/ico.png" alt=""><a href="#">Магазины</a>\n	  </div>\n	</div>\n	\n	\n	<div class="col-xs-4 col-sm-4 col-md-4 col-lg-4 ico-social">\n	<h4>Присоединяйтесь</h4>\n	<a href="#" class="ico-social-1 fac"></a>\n	<a href="#" class="ico-social-1 vk"></a>\n	<a href="#" class="ico-social-1 tw"></a>\n	</div>\n	\n	\n	  </div>\n	</div>\n	\n\n				  \n				  </div>\n\n		</div>	\n	\n	\n\n\n<script type="text/javascript">\n\nfunction parallaxScroll(){\n    var scrolled = $(window).scrollTop();\n    $(''#parallax-bg1'').css(''top'',(0-(scrolled*.225))+''px'');\n    $(''#parallax-bg3'').css(''top'',(0-(scrolled*.185))+''px'');\n}\n\n\n\nfunction getTopOffset(e) { \n	var y = 0;\n	do { y += e.offsetTop; } while (e = e.offsetParent);\n	return y;\n}\nvar block = document.getElementById(''fixblock''); /* fixblock - значение атрибута id блока */\nif ( null != block ) {\n	var topPos = getTopOffset( block );\n	window.onscroll = function() {\n		var newcss = (topPos < window.pageYOffset) ? \n			''top:10%; position: fixed;'' : ''position:static;'';\n		block.setAttribute( ''style'', newcss );\n	}\n}\n\n\n\n</script>\n\n\n\n\n\n\n	</div>\n\n\n			<div id="parallax-bg3">\n				<img id="bg3-1" src="maket/londium/img/home2.png" alt=""/>\n				\n			</div>\n\n\n		<!-- Parallax  midground clouds -->\n			<div id="parallax-bg1">\n				<img id="bg1-1" src="maket/londium/img/home1.png" alt=""/>\n			</div>\n\n</body>\n</html>', 0, 'a:0:{}', 0, '');
