INSERT INTO `modxx_site_plugins` (`id`, `source`, `property_preprocess`, `name`, `description`, `editor_type`, `category`, `cache_type`, `plugincode`, `locked`, `properties`, `disabled`, `moduleguid`, `static`, `static_file`) VALUES
(1, 1, 0, 'miniShop2', '', 0, 7, 0, 'switch ($modx->event->name) {\r\n\r\n	case ''OnManagerPageBeforeRender'':\r\n		$modx23 = !empty($modx->version) && version_compare($modx->version[''full_version''], ''2.3.0'', ''>='');\r\n		$modx->controller->addHtml(''<script type="text/javascript">\r\n			Ext.onReady(function() {\r\n				MODx.modx23 = ''.(int)$modx23.'';\r\n			});\r\n		</script>'');\r\n		if (!$modx23) {\r\n			$modx->controller->addCss(MODX_ASSETS_URL . ''components/minishop2/css/mgr/bootstrap.min.css'');\r\n		}\r\n		$modx->controller->addCss(MODX_ASSETS_URL . ''components/minishop2/css/mgr/main.css'');\r\n		break;\r\n\r\n	case ''OnHandleRequest'':\r\n	case ''OnLoadWebDocument'':\r\n		$isAjax = !empty($_SERVER[''HTTP_X_REQUESTED_WITH'']) && $_SERVER[''HTTP_X_REQUESTED_WITH''] == ''XMLHttpRequest'';\r\n\r\n		if (empty($_REQUEST[''ms2_action'']) || ($isAjax && $modx->event->name != ''OnHandleRequest'') || (!$isAjax && $modx->event->name != ''OnLoadWebDocument'')) {return;}\r\n		$action = trim($_REQUEST[''ms2_action'']);\r\n		$ctx = !empty($_REQUEST[''ctx'']) ? (string) $_REQUEST[''ctx''] : ''web'';\r\n		if ($ctx != ''web'') {$modx->switchContext($ctx);}\r\n\r\n		/* @var miniShop2 $miniShop2 */\r\n		$miniShop2 = $modx->getService(''minishop2'');\r\n		$miniShop2->initialize($ctx, array(''json_response'' => $isAjax));\r\n		if (!($miniShop2 instanceof miniShop2)) {\r\n			@session_write_close();\r\n			exit(''Could not initialize miniShop2'');\r\n		}\r\n\r\n		switch ($action) {\r\n			case ''cart/add'': $response = $miniShop2->cart->add(@$_POST[''id''], @$_POST[''count''], @$_POST[''options'']); break;\r\n			case ''cart/change'': $response = $miniShop2->cart->change(@$_POST[''key''], @$_POST[''count'']); break;\r\n			case ''cart/remove'': $response = $miniShop2->cart->remove(@$_POST[''key'']); break;\r\n			case ''cart/clean'': $response = $miniShop2->cart->clean(); break;\r\n			case ''cart/get'': $response = $miniShop2->cart->get(); break;\r\n			case ''order/add'': $response = $miniShop2->order->add(@$_POST[''key''], @$_POST[''value'']); break;\r\n			case ''order/submit'': $response = $miniShop2->order->submit($_POST); break;\r\n			case ''order/getcost'': $response = $miniShop2->order->getcost(); break;\r\n			case ''order/getrequired'': $response = $miniShop2->order->getDeliveryRequiresFields(@$_POST[''id'']); break;\r\n			case ''order/clean'': $response = $miniShop2->order->clean(); break;\r\n			case ''order/get'': $response = $miniShop2->order->get(); break;\r\n			default:\r\n				$message = ($_REQUEST[''ms2_action''] != $action)\r\n					? ''ms2_err_register_globals''\r\n					: ''ms2_err_unknown'';\r\n				$response = $miniShop2->error($message);\r\n		}\r\n\r\n		if ($isAjax) {\r\n			@session_write_close();\r\n			exit($response);\r\n		}\r\n		break;\r\n\r\n	case ''OnWebPageInit'':\r\n		/* @var msCustomerProfile $profile */\r\n		$referrerVar = $modx->getOption(''ms2_referrer_code_var'', null, ''msfrom'', true);\r\n		$cookieVar = $modx->getOption(''ms2_referrer_cookie_var'', null, ''msreferrer'', true);\r\n		$cookieTime = $modx->getOption(''ms2_referrer_time'', null, 86400 * 365, true);\r\n\r\n		if (!$modx->user->isAuthenticated() && !empty($_REQUEST[$referrerVar])) {\r\n			$code = trim($_REQUEST[$referrerVar]);\r\n			if ($profile = $modx->getObject(''msCustomerProfile'', array(''referrer_code'' => $code))) {\r\n				$referrer = $profile->id;\r\n				setcookie($cookieVar, $referrer, time() + $cookieTime);\r\n			}\r\n		}\r\n		elseif ($modx->user->isAuthenticated() && !empty($_COOKIE[$cookieVar])) {\r\n			if ($profile = $modx->getObject(''msCustomerProfile'', $modx->user->id)) {\r\n				if (!$profile->get(''referrer_id'') && $_COOKIE[$cookieVar] != $modx->user->id) {\r\n					$profile->set(''referrer_id'', $_COOKIE[$cookieVar]);\r\n					$profile->save();\r\n				}\r\n			}\r\n			setcookie($cookieVar, '''', time() - $cookieTime);\r\n		}\r\n		break;\r\n\r\n	case ''msOnChangeOrderStatus'':\r\n		if (empty($status) || $status != 2) {return;}\r\n\r\n		/** @var modUser $user */\r\n		if ($user = $order->getOne(''User'')) {\r\n			$q = $modx->newQuery(''msOrder'', array(''type'' => 0));\r\n			$q->innerJoin(''modUser'', ''modUser'', array(''`modUser`.`id` = `msOrder`.`user_id`''));\r\n			$q->innerJoin(''msOrderLog'', ''msOrderLog'', array(\r\n				''`msOrderLog`.`order_id` = `msOrder`.`id`'',\r\n				''msOrderLog.action'' => ''status'',\r\n				''msOrderLog.entry'' => $status,\r\n			));\r\n			$q->where(array(''msOrder.user_id'' => $user->id));\r\n			$q->groupby(''msOrder.user_id'');\r\n			$q->select(''SUM(`msOrder`.`cost`)'');\r\n			if ($q->prepare() && $q->stmt->execute()) {\r\n				$spent = $q->stmt->fetch(PDO::FETCH_COLUMN);\r\n				/** @var msCustomerProfile $profile */\r\n				if ($profile = $modx->getObject(''msCustomerProfile'', $user->id)) {\r\n					$profile->set(''spent'', $spent);\r\n					$profile->save();\r\n				}\r\n			}\r\n		}\r\n		break;\r\n}', 0, NULL, 0, '', 0, 'core/components/minishop2/elements/plugins/plugin.minishop2.php'),
(2, 1, 0, 'pdoTools', '', 0, 8, 0, '/** @var modX $modx */\nswitch ($modx->event->name) {\n\n    case ''OnMODXInit'':\n        $fqn = $modx->getOption(''pdoTools.class'', null, ''pdotools.pdotools'', true);\n        $path = $modx->getOption(''pdotools_class_path'', null, MODX_CORE_PATH . ''components/pdotools/model/'', true);\n        $modx->loadClass($fqn, $path, false, true);\n\n        $fqn = $modx->getOption(''pdoFetch.class'', null, ''pdotools.pdofetch'', true);\n        $path = $modx->getOption(''pdofetch_class_path'', null, MODX_CORE_PATH . ''components/pdotools/model/'', true);\n        $modx->loadClass($fqn, $path, false, true);\n        break;\n\n    case ''OnBeforeSaveWebPageCache'':\n        if (!empty($modx->config[''fenom_jscripts''])) {\n            foreach ($modx->config[''fenom_jscripts''] as $key => $value) {\n                unset($modx->resource->_jscripts[$key]);\n            }\n        }\n        if (!empty($modx->config[''fenom_sjscripts''])) {\n            foreach ($modx->config[''fenom_sjscripts''] as $key => $value) {\n                unset($modx->resource->_sjscripts[$key]);\n            }\n        }\n        if (!empty($modx->config[''fenom_loadedscripts''])) {\n            foreach ($modx->config[''fenom_loadedscripts''] as $key => $value) {\n                unset($modx->resource->_loadedjscripts[$key]);\n            }\n        }\n        break;\n\n    case ''OnSiteRefresh'':\n        /** @var pdoTools $pdoTools */\n        if ($pdoTools = $modx->getService(''pdoTools'')) {\n            if ($pdoTools->clearFileCache()) {\n                $modx->log(modX::LOG_LEVEL_INFO, $modx->lexicon(''refresh_default'') . '': pdoTools'');\n            }\n        }\n        break;\n}', 0, NULL, 0, '', 0, 'core/components/pdotools/elements/plugins/plugin.pdotools.php'),
(3, 1, 1, 'shk_send_sms', 'Отправка SMS при смене статуса заказа', 0, 0, 0, '/*\n * Плагин для отправки SMS со статусом заказа Shopkeeper (Revo)\n *\n * Набор параметров:\n * sms_fieldname - Название доп. поля в профиле пользователя, которое разрешает отправку ему SMS сообщений (по умолчанию sms_enable)\n * sms_url - URL адрес API SMS сервиса  (по умолчанию http://sendmsg.ru/api.php)\n * sms_login - Логин с сайта SMS сервиса\n * sms_secret - Секретный ключ с сайта SMS сервиса\n * sms_status_tpl - Шаблон текста SMS для статуса "Новый" (по умолчанию чанк sms_new)\n * sms_status1_tpl - Шаблон текста SMS для статуса "Принят к оплате" (по умолчанию чанк sms_pay_ok)\n * sms_status2_tpl - Шаблон текста SMS для статуса "Отправлен" (по умолчанию чанк sms_sent)\n * sms_status3_tpl - Шаблон текста SMS для статуса "Выполнен" (по умолчанию чанк sms_done)\n * sms_status4_tpl - Шаблон текста SMS для статуса "Отменен" (по умолчанию чанк sms_body)\n * sms_status5_tpl - Шаблон текста SMS для статуса "Оплата получена" (по умолчанию чанк sms_pay_done)\n * sms_translit - Транслитировать сообщение? (по умолчанию Нет)\n *\n * Событие:\n * OnSHKChangeStatus\n *\n * @autor Николаев Константин <sector99@bk.ru>\n * @version 0.2\n */\n\n$sms_url = $scriptProperties[''sms_url''];\n$sms_login = $scriptProperties[''sms_login'']; //Логин\n$sms_secret = $scriptProperties[''sms_secret'']; //Секретный ключ\n/* TODO: Добавить имя отправителя и режим тестирования */\n//$sms_sender = $scriptProperties[''sms_sender'']; //имя отправителя\n//$sms_testmode = $scriptProperties[''sms_test''];\n$status_tpl = array();\n$status_tpl[0] = empty($scriptProperties[''sms_status_tpl''])  ? '''' : $scriptProperties[''sms_status_tpl''];\n$status_tpl[1] = empty($scriptProperties[''sms_status1_tpl'']) ? '''' : $scriptProperties[''sms_status1_tpl''];\n$status_tpl[2] = empty($scriptProperties[''sms_status2_tpl'']) ? '''' : $scriptProperties[''sms_status2_tpl''];\n$status_tpl[3] = empty($scriptProperties[''sms_status3_tpl'']) ? '''' : $scriptProperties[''sms_status3_tpl''];\n$status_tpl[4] = empty($scriptProperties[''sms_status4_tpl'']) ? '''' : $scriptProperties[''sms_status4_tpl''];\n$status_tpl[5] = empty($scriptProperties[''sms_status5_tpl'']) ? '''' : $scriptProperties[''sms_status5_tpl''];\n$sms_user_fieldname = $scriptProperties[''sms_fieldname'']; //Доп. поле пользователя\n$sms_translit = $scriptProperties[''sms_translit'']; //Транслитировать\n\n$site_name = $modx->getOption(''site_name'');\n$site_url = $modx->getOption(''site_url'');\n\nif ($modx->event->name == ''OnSHKChangeStatus'') {\n\n	$order_id = isset($order_id) ? $order_id : '''';\n	$status = isset($status) ? intval($status) : '''';\n\n	require_once MODX_CORE_PATH."components/shopkeeper/model/shopkeeper.class.php";\n	require_once MODX_CORE_PATH."components/shopkeeper/model/shk_mgr.class.php";\n	$SHKmanager = new SHKmanager($modx);\n	$SHKmanager->getModConfig(); //Конфигурация Shopkeeper\n	$order = $modx->getObject(''SHKorder'', $order_id); //Параметры заказа\n\n	$order_data = array();\n	if (!empty($order)) {\n		$order_data = $order->toArray();\n	}\n\n	$statusName = $SHKmanager->config[''statuses''];\n\n	/**\n	 * Транслитрация кириллицы\n	 * @param $str\n	 * @return string\n	 */\n	function translit($str){\n		$tr = array(\n			"А"=>"A","Б"=>"B","В"=>"V","Г"=>"G",\n			"Д"=>"D","Е"=>"E","Ж"=>"J","З"=>"Z","И"=>"I",\n			"Й"=>"Y","К"=>"K","Л"=>"L","М"=>"M","Н"=>"N",\n			"О"=>"O","П"=>"P","Р"=>"R","С"=>"S","Т"=>"T",\n			"У"=>"U","Ф"=>"F","Х"=>"H","Ц"=>"TS","Ч"=>"CH",\n			"Ш"=>"SH","Щ"=>"SCH","Ъ"=>"","Ы"=>"Y","Ь"=>"",\n			"Э"=>"E","Ю"=>"YU","Я"=>"YA","а"=>"a","б"=>"b",\n			"в"=>"v","г"=>"g","д"=>"d","е"=>"e","ж"=>"j",\n			"з"=>"z","и"=>"i","й"=>"y","к"=>"k","л"=>"l",\n			"м"=>"m","н"=>"n","о"=>"o","п"=>"p","р"=>"r",\n			"с"=>"s","т"=>"t","у"=>"u","ф"=>"f","х"=>"h",\n			"ц"=>"ts","ч"=>"ch","ш"=>"sh","щ"=>"sch","ъ"=>"y",\n			"ы"=>"y","ь"=>"","э"=>"e","ю"=>"yu","я"=>"ya"\n		);\n	return strtr($str,$tr);\n	}\n\n	/**\n	 * Проверяет, что пользователь разрешил отправку SMS\n	 * @return bool\n	 */\n	function check($user_id, $fieldname) {\n		global $modx;\n		if (!$user = $modx->getObject(''modUser'', $user_id)) {\n			$modx->log(modX::LOG_LEVEL_ERROR, ''Пользователь не найден'');\n			return false;\n		}\n		if (!$profile = $user->getOne(''Profile'')) {\n			$modx->log(modX::LOG_LEVEL_ERROR, ''Ошибка получения профиля'');\n			return false;\n		}\n		$extended = $profile->get(''extended'');\n		return ($extended[$fieldname]) ? true : false;\n	}\n\n	function chooseTpl($status_tpl, $status) {\n		global $modx;\n		$tpl = $status_tpl[$status];\n\n		if(substr($tpl, 0, 5) == "@FILE"){\n			$tpl_file = MODX_BASE_PATH . substr($tpl, 6);\n			$template = file_get_contents($tpl_file);\n		} else if (substr($tpl, 0, 5) == "@CODE"){\n			$template = substr($tpl, 5);\n		} else if ($modx->getChunk($tpl) != ""){\n		 	$template = $modx->getChunk($tpl);\n		} else {\n			$template = false;\n		}\n			return $template;\n	}\n\n	if (check($order_data[''userid''], $sms_user_fieldname)) {\n		$body = chooseTpl($status_tpl, $status);\n\n		$chunkArr = array(\n			''orderID'' => $order_id,\n			''status_name'' => $statusName[$status][0],\n			''site_name'' => $site_name,\n			''site_url'' => $site_url\n		);\n\n		foreach(array_merge($chunkArr,$order_data) as $key => $value){\n			$body = str_replace(''[[+''.$key.'']]'', $value, $body);\n		}\n\n		$body = preg_replace(''/[\\r\\n]+/u'','''',strip_tags($body));\n		if ($sms_translit) translit($body);\n\n		$params[''user''] = $sms_login;\n		$params[''hash''] = md5($sms_login."_".$sms_secret);\n		$params[''method''] = ''send'';\n		$params[''text''] = urlencode($body);\n		$params[''phone''] = $order_data[''phone''];\n\n		//Отправка SMS\n		if(mb_strlen($params[''phone'']) > 0 && !empty($params[''text''])){\n			$ch = curl_init();\n			$curl_url = $sms_url;\n			curl_setopt($ch, CURLOPT_URL, $curl_url);\n			curl_setopt($ch, CURLOPT_REFERER, $site_url);\n			curl_setopt($ch, CURLOPT_POST, 1);\n			curl_setopt($ch, CURLOPT_POSTFIELDS, http_build_query($params, '''', ''&''));\n			curl_setopt($ch, CURLOPT_RETURNTRANSFER,1);\n			curl_setopt($ch, CURLOPT_FOLLOWLOCATION, 1);\n			$resp = curl_exec($ch);\n\n			if(!$resp){\n				$modx->log(modX::LOG_LEVEL_ERROR, curl_error($ch).''(''.curl_errno($ch).'')'');\n				exit;\n			} else {\n				$resp = json_decode($resp, 1);\n				if ($resp[''response''][''err_code''] != 0) {\n					$modx->log(modX::LOG_LEVEL_ERROR, ''Ошибка SMS сервиса: ''.$resp[''response''][''msg'']);\n				}\n			}\n\n			curl_close($ch);\n		}\n	}\n}', 0, 'a:11:{s:13:"sms_fieldname";a:7:{s:4:"name";s:13:"sms_fieldname";s:4:"desc";s:158:"Название доп. поля в профиле пользователя, которое разрешает отправку ему SMS сообщений";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:10:"sms_enable";s:7:"lexicon";N;s:4:"area";s:0:"";}s:9:"sms_login";a:7:{s:4:"name";s:9:"sms_login";s:4:"desc";s:32:"Логин в SMS сервисе";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:0:"";s:7:"lexicon";N;s:4:"area";s:0:"";}s:10:"sms_secret";a:7:{s:4:"name";s:10:"sms_secret";s:4:"desc";s:49:"Секретный ключ в SMS сервисе";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:0:"";s:7:"lexicon";N;s:4:"area";s:0:"";}s:15:"sms_status1_tpl";a:7:{s:4:"name";s:15:"sms_status1_tpl";s:4:"desc";s:82:"Шаблон текста SMS для статуса "Принят к оплате"";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:10:"sms_pay_ok";s:7:"lexicon";N;s:4:"area";s:0:"";}s:15:"sms_status2_tpl";a:7:{s:4:"name";s:15:"sms_status2_tpl";s:4:"desc";s:72:"Шаблон текста SMS для статуса "Отправлен"";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:8:"sms_sent";s:7:"lexicon";N;s:4:"area";s:0:"";}s:15:"sms_status3_tpl";a:7:{s:4:"name";s:15:"sms_status3_tpl";s:4:"desc";s:70:"Шаблон текста SMS для статуса "Выполнен"";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:8:"sms_done";s:7:"lexicon";N;s:4:"area";s:0:"";}s:15:"sms_status4_tpl";a:7:{s:4:"name";s:15:"sms_status4_tpl";s:4:"desc";s:68: